CREATE TABLE IF NOT EXISTS `ab_tests` (`id` int AUTO_INCREMENT NOT NULL, `name` varchar(200) NOT NULL, `description` text, `status` varchar(30) DEFAULT 'draft', `startDate` timestamp, `endDate` timestamp, `variants` json, `metrics` json, `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY(`id`));
CREATE TABLE IF NOT EXISTS `admin_activity_log` (`id` int AUTO_INCREMENT NOT NULL, `userId` int, `action` varchar(200) NOT NULL, `targetType` varchar(100), `targetId` int, `details` json, `ipAddress` varchar(50), `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY(`id`));
CREATE TABLE IF NOT EXISTS `admin_notifications` (`id` int AUTO_INCREMENT NOT NULL, `userId` int, `title` varchar(300) NOT NULL, `message` text, `type` varchar(50) DEFAULT 'info', `isRead` tinyint(1) DEFAULT 0, `link` varchar(500), `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY(`id`));

