CREATE TABLE IF NOT EXISTS `cms_pages` (`id` int AUTO_INCREMENT NOT NULL, `title` varchar(300) NOT NULL, `slug` varchar(300) NOT NULL, `description` text, `pageType` varchar(50) DEFAULT 'landing', `status` varchar(30) DEFAULT 'draft', `metaTitle` varchar(300), `metaDescription` text, `template` varchar(100), `createdBy` int, `publishedAt` timestamp, `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY(`id`));
CREATE TABLE IF NOT EXISTS `cms_page_sections` (`id` int AUTO_INCREMENT NOT NULL, `pageId` int NOT NULL, `sectionType` varchar(100) NOT NULL, `title` varchar(300), `content` json, `styles` json, `sortOrder` int DEFAULT 0, `isVisible` tinyint(1) DEFAULT 1, `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY(`id`));
CREATE TABLE IF NOT EXISTS `cms_page_versions` (`id` int AUTO_INCREMENT NOT NULL, `pageId` int NOT NULL, `versionNumber` int DEFAULT 1, `title` varchar(300), `slug` varchar(300), `description` text, `pageType` varchar(50), `sectionsSnapshot` json, `createdBy` int, `note` text, `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY(`id`));
CREATE TABLE IF NOT EXISTS `cms_section_revisions` (`id` int AUTO_INCREMENT NOT NULL, `sectionId` int NOT NULL, `pageId` int, `userId` int, `userName` varchar(200), `action` varchar(100), `fieldChanged` varchar(200), `previousData` json, `newData` json, `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY(`id`));
CREATE TABLE IF NOT EXISTS `cms_section_templates` (`id` int AUTO_INCREMENT NOT NULL, `name` varchar(200) NOT NULL, `description` text, `sectionType` varchar(100), `category` varchar(100), `thumbnail` varchar(500), `content` json, `styles` json, `isDefault` tinyint(1) DEFAULT 0, `usageCount` int DEFAULT 0, `createdBy` int, `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY(`id`));
CREATE TABLE IF NOT EXISTS `cms_style_presets` (`id` int AUTO_INCREMENT NOT NULL, `name` varchar(200) NOT NULL, `description` text, `styles` json, `category` varchar(100), `isDefault` tinyint(1) DEFAULT 0, `createdBy` int, `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY(`id`));

