CREATE TABLE IF NOT EXISTS `coaching_sessions` (`id` int AUTO_INCREMENT NOT NULL, `coachId` int NOT NULL, `learnerId` int NOT NULL, `scheduledAt` timestamp, `duration` int DEFAULT 60, `status` varchar(30) DEFAULT 'scheduled', `type` varchar(50) DEFAULT 'one_on_one', `notes` text, `rating` int, `feedback` text, `meetingUrl` varchar(500), `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY(`id`));
CREATE TABLE IF NOT EXISTS `drip_content` (`id` int AUTO_INCREMENT NOT NULL, `lessonId` int NOT NULL, `unlockType` varchar(50) DEFAULT 'date', `unlockDate` timestamp, `unlockAfterDays` int, `isLocked` tinyint(1) DEFAULT 1, `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY(`id`));
CREATE TABLE IF NOT EXISTS `email_templates` (`id` int AUTO_INCREMENT NOT NULL, `name` varchar(200) NOT NULL, `subject` varchar(500) NOT NULL, `body` text NOT NULL, `type` varchar(50) DEFAULT 'transactional', `variables` json, `isActive` tinyint(1) DEFAULT 1, `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY(`id`));
CREATE TABLE IF NOT EXISTS `media_library` (`id` int AUTO_INCREMENT NOT NULL, `fileName` varchar(300) NOT NULL, `fileKey` varchar(500), `url` text, `mimeType` varchar(100), `fileSize` int DEFAULT 0, `altText` varchar(500), `tags` json, `folder` varchar(200), `uploadedBy` int, `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY(`id`));

