CREATE TABLE IF NOT EXISTS `org_course_assignments` (`id` int AUTO_INCREMENT NOT NULL, `organizationId` int NOT NULL, `courseId` int NOT NULL, `assignedBy` int, `assignedAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY(`id`));
CREATE TABLE IF NOT EXISTS `org_members` (`id` int AUTO_INCREMENT NOT NULL, `organizationId` int NOT NULL, `userId` int NOT NULL, `role` varchar(50) DEFAULT 'member', `status` varchar(30) DEFAULT 'active', `joinedAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY(`id`));
CREATE TABLE IF NOT EXISTS `permissions` (`id` int AUTO_INCREMENT NOT NULL, `name` varchar(200) NOT NULL, `description` text, `module` varchar(100), `action` varchar(100), `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY(`id`));
CREATE TABLE IF NOT EXISTS `role_permissions` (`id` int AUTO_INCREMENT NOT NULL, `role` varchar(50) NOT NULL, `module` varchar(100) NOT NULL, `action` varchar(100) NOT NULL, `allowed` tinyint(1) DEFAULT 1, `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY(`id`));
CREATE TABLE IF NOT EXISTS `roles` (`id` int AUTO_INCREMENT NOT NULL, `name` varchar(100) NOT NULL, `description` text, `isSystem` tinyint(1) DEFAULT 0, `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY(`id`));

